cipher = ""
key = 13
# e -> l. fghijkl

ci = cipher.lower()

alphabet=('a','b','c','d','e','f','g','h','i','j','k','l','m','n','o','p',
          'q','r','s','t','u','v','w','x','y','z', ' ')

def decrypt(key, cipher):
    plaintext = ""  

    for c in cipher:
        plaintext += alphabet[(alphabet.index(c)-key) % 26]
    return plaintext

def find_alph(a, b):
    return (alphabet[(alphabet.index(a) - alphabet.index(b)) % 26])

